import axios from "axios";

export const useApi = () => {
  const uploadCard = async (cardInfo) => {
    const result = fetch(
      "https://reacting-d4273-default-rtdb.firebaseio.com/",
      {
        method: "POST",
        body: cardInfo,
        headers: {
          "Content-Type": "application/json",
        },
      }
    );
  };

  return { uploadCard };
};
