import { Button, Col, Container, Row } from "react-bootstrap";
import Form from "react-bootstrap/Form";
import { useRef } from "react";
import React from "react";

class ItemForm extends React.Component {
  constructor(props) {
    super();
  }

  submitHandler(event) {
    event.preventDefault();

    const enteredTitle = titleInputRef.current.value;
    const enteredPrice = priceInputRef.current.value;
    const enteredDescription = descriptionInputRef.current.value;
    const enteredImage = imageInputRef.current.value;
    const enteredCategory = categoryInputRef.current.value;

    const Item = {
      title: enteredTitle,
      price: enteredPrice,
      description: enteredDescription,
      image: enteredImage,
      category: enteredCategory,
    };

    console.log("Values: ", Item);
  }

  render() {
    return (
      <Form onSubmit={this.submitHandler}>
        <Form.Group className="mb-3" controlId="formTitle">
          <Form.Label> Title </Form.Label>
          <Form.Control type="text" placeholder=". . . " ref={titleInputRef} />
        </Form.Group>
        <Form.Group className="mb-3" controlId="formPrice">
          <Form.Label> Price </Form.Label>
          <Form.Control type="number" placeholder="0.00" ref={priceInputRef} />
        </Form.Group>
        <Form.Group className="mb-3" controlId="formDescription">
          <Form.Label> Description </Form.Label>
          <Form.Control as="textarea" rows={3} ref={descriptionInputRef} />
        </Form.Group>
        <Form.Group controlId="formFile" className="mb-3">
          <Form.Label>Insert image JPEG or PNG</Form.Label>
          <Form.Control type="file" ref={imageInputRef} />
        </Form.Group>
        <Form.Group controlId="categorySelect" className="mb-3">
          <Form.Label> Select Category </Form.Label>
          <Form.Select
            aria-label="Default select example"
            ref={categoryInputRef}
          >
            <option>------</option>
            <option value="Animal">Animal</option>
            <option value="Human">Human</option>
            <option value="Machine">Machine</option>
          </Form.Select>
        </Form.Group>
        <Button variant="primary" type="submit">
          Submit
        </Button>
      </Form>
    );
  }
}

function HomeForm() {
  const titleInputRef = useRef();
  const priceInputRef = useRef();
  const descriptionInputRef = useRef();
  const imageInputRef = useRef();
  const categoryInputRef = useRef();

  return (
    <Container fluid>
      <Row>
        <Col>
          <ItemForm></ItemForm>
        </Col>
      </Row>
    </Container>
  );
}

export default HomeForm;
