export const useApi = () => {
  const uploadCard = async (cardInfo) => {
    const result = fetch(
      "https://reacting-d4273-default-rtdb.firebaseio.com/cards.json",
      {
        method: "POST",
        body: { cardInfo },
        headers: {
          "Content-Type": "application/json",
        },
      }
    );
    return result;
  };

  return { uploadCard };
};
