import axios from "axios";

export const useApi = () => {
  axios.defaults.baseURL = `https://reacting-d4273-default-rtdb.firebaseio.com/`;

  const uploadCard = async (cardInfo) => {
    const result = await axios
      .post("cards", { cardInfo })
      .then(function (response) {
        console.log(response);
      })
      .catch(function (error) {
        console.log(error);
      });
  };

  return { uploadCard };
};
