import { useHistory } from "react-router-dom";

export const useApi = () => {
  const uploadCard = async (cardInfo) => {
    const result = fetch(
      "https://reacting-d4273-default-rtdb.firebaseio.com/cards.json",
      {
        method: "POST",
        body: JSON.stringify(cardInfo),
        headers: {
          "Content-Type": "application/json",
        },
      }
    ).then(() => {});
    return result;
  };

  return { uploadCard };
};
