import { Button, Col, Container, Row } from "react-bootstrap";
import Form from "react-bootstrap/Form";
import { useRef, useState } from "react";
import React from "react";

function ItemForm(props) {
  const titleInputRef = useRef(props.titleInputRef);
  const priceInputRef = useRef(props.priceInputRef);
  const descriptionInputRef = useRef(props.descriptionInputRef);
  const imageInputRef = useRef(props.imageInputRef);
  const categoryInputRef = useRef(props.categoryInputRef);

  submitHandler(event);
  {
    event.preventDefault();

    const enteredTitle = this.state.titleInputRef.current.value;
    const enteredPrice = this.state.priceInputRef.current.value;
    const enteredDescription = this.state.descriptionInputRef.current.value;
    const enteredImage = this.state.imageInputRef.current.value;
    const enteredCategory = this.state.categoryInputRef.current.value;

    const Item = {
      title: enteredTitle,
      price: enteredPrice,
      description: enteredDescription,
      image: enteredImage,
      category: enteredCategory,
    };

    console.log("Values: ", Item);
  }

  return (
    <Form onSubmit={this.submitHandler}>
      <Form.Group className="mb-3" controlId="formTitle">
        <Form.Label> Title </Form.Label>
        <Form.Control
          type="text"
          placeholder=". . . "
          ref={this.state.titleInputRef}
        />
      </Form.Group>
      <Form.Group className="mb-3" controlId="formPrice">
        <Form.Label> Price </Form.Label>
        <Form.Control
          type="number"
          placeholder="0.00"
          ref={this.state.priceInputRef}
        />
      </Form.Group>
      <Form.Group className="mb-3" controlId="formDescription">
        <Form.Label> Description </Form.Label>
        <Form.Control
          as="textarea"
          rows={3}
          ref={this.state.descriptionInputRef}
        />
      </Form.Group>
      <Form.Group controlId="formFile" className="mb-3">
        <Form.Label>Insert image JPEG or PNG</Form.Label>
        <Form.Control type="file" ref={this.state.imageInputRef} />
      </Form.Group>
      <Form.Group controlId="categorySelect" className="mb-3">
        <Form.Label> Select Category </Form.Label>
        <Form.Select
          aria-label="Default select example"
          ref={this.state.categoryInputRef}
        >
          <option>------</option>
          <option value="Animal">Animal</option>
          <option value="Human">Human</option>
          <option value="Machine">Machine</option>
        </Form.Select>
      </Form.Group>
      <Button variant="primary" type="submit">
        Submit
      </Button>
    </Form>
  );
}

function HomeForm() {
  const titleInputRef = useRef();
  const priceInputRef = useRef();
  const descriptionInputRef = useRef();
  const imageInputRef = useRef();
  const categoryInputRef = useRef();

  return (
    <Container fluid>
      <Row>
        <Col>
          <ItemForm
            titleInputRef={titleInputRef}
            priceInputRef={priceInputRef}
            descriptionInputRef={descriptionInputRef}
            imageInputRef={imageInputRef}
            categoryInputRef={categoryInputRef}
          ></ItemForm>
        </Col>
      </Row>
    </Container>
  );
}

export default HomeForm;
